import React from 'react'
import {quoteData} from './quoteData';

const iniRandNum = Math.floor(Math.random() * quoteData.length);

class QuoteBox extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {
            quote: quoteData[iniRandNum].quote,
            author: quoteData[iniRandNum].author
        }

        this.newQuote = this.newQuote.bind(this);
    }

    newQuote() {
        const randNum = Math.floor(Math.random() * quoteData.length);
        console.log(randNum)
        this.setState({
            quote: quoteData[randNum].quote,
            author: quoteData[randNum].author,
        })

    }

    render() {
        return (
            <div id="quote-box">
                <h1 id='text'><span className="quoteSymbol">"</span>{this.state.quote}<span className="quoteSymbol">"</span></h1>
                <p id="author">{this.state.author}</p>
                <button id="new-quote" onClick={this.newQuote}>New quote</button>
                <div id="share">
                    <a id="tweet-quote" href={"https://twitter.com/intent/tweet?text=" + this.state.quote}>Tweet</a>
                </div>
            </div>
        )
    }
}


export default QuoteBox
