import React from 'react'
import quoteData from './quoteData';

class Quote extends React.Component {
    constructor(props) {
        super(props);
        
        this.state = {
            quote: 'Anytime someone tells me that I can\'t do something, I want to do it more.'
        }

        this.newQuote = this.newQuote.bind(this)
    }

    newQuote() {
        const randNum = Math.floor(Math.random() * quoteData.length);
        console.log(randNum)
        this.setState(quoteData[randNum].quote)
    }


    render() {
        return (
            <div>
                <h1 id='quote'>{this.state.quote}</h1>
                <button onClick={this.newQuote}>New quote</button>
            </div>
        )
    }
}


export default Quote
